import { useState } from "react";
import Css from '../App.css';

function PlayerManager() {
  const [players, setPlayers] = useState([]);
  const [showInput, setShowInput] = useState(false);
  const [newName, setNewName] = useState("");

  const addPlayer = () => {
    if (newName.trim() !== "" && players.length < 6) {
      setPlayers([...players, { name: newName, score: 0 }]); // default score = 0
      setNewName("");
      setShowInput(false);
    }
  };

  return (
    <div className="playerCard">
      {/* Dynamic title */}
      <h1 className="text-xl font-bold mb-2">
        {players.length === 0 ? "Add Players" : "Players"}
      </h1>

      {/* Player list with vertical divider */}
      <div className="border rounded-md shadow-sm overflow-hidden">
        {/* Header row */}
        <div className="flex bg-gray-200">
          <div className="w-4/5 p-2 font-semibold">Players</div>
          <div className="w-1/5 p-2 font-semibold text-center border-l">Score</div>
        </div>

        {/* Player rows */}
        {players.map((player, index) => (
          <div key={index} className="flex border-t">
            <div className="w-4/5 p-2">{player.name}</div>
            <div className="w-1/5 p-2 text-center border-l">{player.score}</div>
          </div>
        ))}
      </div>

      {/* Input field to add player */}
      {showInput && (
        <div className="addPlayerInput flex space-x-2 mt-3">
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
            placeholder="Enter player name"
            className="border rounded-md px-2 py-1 flex-1"
          />
          <button
            onClick={addPlayer}
            className="addPlayerButton bg-green-500 text-white px-3 py-1 rounded-md"
          >
            Add
          </button>
        </div>
      )}

      {/* "+" button (disabled if max reached) */}
      {players.length < 6 && !showInput && (
        <button
          onClick={() => setShowInput(true)}
          className="bg-blue-500 text-white px-3 py-1 rounded-full text-lg mt-3"
        >
          +
        </button>
      )}

      {/* Show message if max reached */}
      {players.length >= 6 && (
        <p className="text-red-500 mt-2">Max 6 players reached</p>
      )}
    </div>
  );
}

export default PlayerManager;
