import { useState } from "react";

function PlayerManager() {
  const [players, setPlayers] = useState([]);
  const [showInput, setShowInput] = useState(false);
  const [newName, setNewName] = useState("");

  const addPlayer = () => {
    if (newName.trim() !== "" && players.length < 6) {
      setPlayers([...players, { name: newName, score: 0 }]); // default score = 0
      setNewName("");
      setShowInput(false);
    }
  };

  return (
    <div>
      <h1 className="text-xl font-bold mb-2">Players</h1>

      {/* Existing players */}
      <div className="space-y-2 mb-4">
        {players.map((player, index) => (
          <div
            key={index}
            className="p-2 border rounded-md shadow-sm bg-gray-100"
          >
            <h2>{player.name}</h2>
            <p>Score: {player.score}</p>
          </div>
        ))}
      </div>

      {/* Input field to add player */}
      {showInput && (
        <div className="flex space-x-2 mb-2">
          <input
            type="text"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
            placeholder="Enter player name"
            className="border rounded-md px-2 py-1 flex-1"
          />
          <button
            onClick={addPlayer}
            className="bg-green-500 text-white px-3 py-1 rounded-md"
          >
            Add
          </button>
        </div>
      )}

      {/* "+" button (disabled if max reached) */}
      {players.length < 6 && !showInput && (
        <button
          onClick={() => setShowInput(true)}
          className="bg-blue-500 text-white px-3 py-1 rounded-full text-lg"
        >
          +
        </button>
      )}

      {/* Show message if max reached */}
      {players.length >= 6 && (
        <p className="text-red-500 mt-2">Max 6 players reached</p>
      )}
    </div>
  );
}

export default PlayerManager;
