import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import css from '../App.css';
import React from 'react';
import ChallengeCard, { LengthCard } from '../components/ChallengeCard';
import StartGameBtn from '../components/StartGameBtn';

 

export default function Challenges() {
  const [selectedChallenge, setSelectedChallenge] = useState(null);
  const navigate = useNavigate();

  const routeMap = {
    "Wiki Race": "/wikirace",
    "Unique Hunt (monster)": "/unique-monster",
    "Unique Hunt (clue scroll)": "/unique-clue",
    "Red Click Race": "/red-click",
    "Inventory Puzzle": "/inventory-puzzle",
    "Item Hunt": "/item-hunt",
    "Location Guesser": "/location-guesser",
    "OSRS Trivia": "/trivia",
    "Resource Rush": "/resource-rush",
    "Boss Speedrun": "/boss-speedrun"
  };

  const handleStart = () => {
    const path = routeMap[selectedChallenge];
    if (path) navigate(path);
  };
  return (
    <div className="ChallengeCard-container">
      <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
        {challenges.map((challenge, idx) => (
          <li key={idx}>
            <label>
              <input
                type="checkbox"
                checked={selectedChallenge === challenge}
                onChange={() => handleCheckboxChange(challenge)}
              />
              {challenge}
            </label>
          </li>
        ))}
      </ul>
    </div>
  );
}
